import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

import * as UserServer from "./UserServer";

const UserForm = () => {
    const navigate = useNavigate();
    const params = useParams();

    //console.log(params);

    const initialState = { id: 0, email: "", password: "", nick: "", name: "" };

    const [user, setUser] = useState(initialState);

    const handleInputChange = (e) => {
        //console.log(e.target.name);
        //console.log(e.target.value);
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        //console.log(user);
        try {
            let res;
            if (!params.id) { //Modo de registro
                res = await UserServer.registerUser(user);
                const data = await res.json();
                //console.log(data);
                if (data.message === "Success") {
                    setUser(initialState);
                }
            } else {
                await UserServer.updateUser(params.id, user);
            }
            navigate("/");
        } catch (error) {
            console.log(error);
        }
    };

    const getUser = async (userId) => {

        try {
            const res = await UserServer.getUser(userId);
            const data = await res.json();
            //console.log(data);
            const { email, password, nick, name } = data.user;
            setUser({ email, password, nick, name });
        } catch (error) {
            console.log(error);
        };
    };

    useEffect(() => {
        if (params.id) {
            getUser(params.id);
        }
        // eslint-disable-next-line
    }, []);

    return (
        <div className="col-md-3 mx-auto">
            <h2 className="mb-3 text-center">User</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">Correo</label>
                    <input type="text" name="email" value={user.email} onChange={handleInputChange} className="form-control" minLength="2" maxLength="50" autoFocus required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Foundation</label>
                    <input type="number" name="foundation" value={user.foundation} onChange={handleInputChange} className="form-control" min="1900" max="2024" required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Website</label>
                    <input type="url" name="website" value={user.website} onChange={handleInputChange} className="form-control" maxLength="100" required />
                </div>
                <div className="d-grid gap-2">
                    {
                        params.id ? (
                            <button type="submit" className="btn btn-block btn-primary">
                                Update
                            </button>
                        ) : (
                            <button type="submit" className="btn btn-block btn-success">
                                Register
                            </button>
                        )}
                </div>
            </form>
        </div>
    )
};

export default UserForm;
